"use client";
import { useEffect, useState } from "react";

import { StoreProvider } from "./components/StoreProvider/StoreProvider";
import Menu from "@/components/Menu/Menu";
import Cart from "@/components/Cart/Cart";

import { ReactLenis } from "lenis/react";

export default function ClientLayout({ children }) {
  const [isMobile, setIsMobile] = useState(false);
  const [isStoreReady, setIsStoreReady] = useState(false);

  useEffect(() => {
    setIsStoreReady(true);

    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 900);
    };

    checkMobile();

    window.addEventListener("resize", checkMobile);

    return () => window.removeEventListener("resize", checkMobile);
  }, []);

  const scrollSettings = isMobile
    ? {
        duration: 1,
        easing: (t) => Math.min(1, 1.001 - Math.pow(2, -10 * t)),
        direction: "vertical",
        gestureDirection: "vertical",
        smooth: true,
        smoothTouch: true,
        touchMultiplier: 1.5,
        infinite: false,
        lerp: 0.05,
        wheelMultiplier: 1,
        orientation: "vertical",
        smoothWheel: true,
        syncTouch: true,
      }
    : {
        duration: 1.2,
        easing: (t) => Math.min(1, 1.001 - Math.pow(2, -10 * t)),
        direction: "vertical",
        gestureDirection: "vertical",
        smooth: true,
        smoothTouch: false,
        touchMultiplier: 2,
        infinite: false,
        lerp: 0.1,
        wheelMultiplier: 1,
        orientation: "vertical",
        smoothWheel: true,
        syncTouch: true,
      };

  return (
    <ReactLenis root options={scrollSettings}>
      <StoreProvider>
        {isStoreReady && (
          <>
            <Menu />
            <Cart />
          </>
        )}
        {children}
      </StoreProvider>
    </ReactLenis>
  );
}

export const generateSlug = (name) => {
  return name.toLowerCase().replace(/\s+/g, "-");
};

export const findProductBySlug = (products, slug) => {
  return products.find((product) => generateSlug(product.name) === slug);
};

export const findArticleBySlug = (articles, slug) => {
  return articles.find((article) => generateSlug(article.title) === slug);
};

@import url("https://fonts.cdnfonts.com/css/pp-neue-montreal");

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  -webkit-tap-highlight-color: transparent;
}

html,
body {
  height: 100%;
  width: 100%;
}

body {
  font-family: "PP Neue Montreal", sans-serif;
  background-color: #000;
}

html.lenis {
  height: auto;
}

.lenis.lenis-smooth {
  scroll-behavior: auto;
}

.lenis.lenis-smooth [data-lenis-prevent] {
  overscroll-behavior: contain;
}

.lenis.lenis-stopped {
  overflow: hidden;
}

.lenis.lenis-scrolling iframe {
  pointer-events: none;
}

::view-transition-old(root),
::view-transition-new(root) {
  animation: none !important;
}

::view-transition-group(root) {
  z-index: auto !important;
}

::view-transition-image-pair(root) {
  isolation: isolate;
  will-change: transform, opacity, clip-path;
  z-index: 1;
}

::view-transition-new(root) {
  z-index: 10000;
  animation: none !important;
}

::view-transition-old(root) {
  z-index: 1;
  animation: none !important;
}

img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

a,
p {
  position: relative;
  color: #000;
  display: block;
  text-decoration: none;
  text-transform: uppercase;
  font-family: "PP Neue Montreal", sans-serif;
  font-size: 0.75rem;
  font-weight: 600;
  letter-spacing: -0.008rem;
  line-height: 1;
  will-change: transform;
  -webkit-font-smoothing: antialiased;
}

.revealer {
  position: relative;
  width: max-content;
  clip-path: polygon(0 0, 100% 0, 100% 100%, 0% 100%);
}

.p-25 {
  width: 100%;
  height: 25vh;
  background-color: #fff;
}

.p-50 {
  width: 100%;
  height: 50vh;
  background-color: #fff;
}

.home-page {
  position: relative;
  width: 100vw;
  height: 100svh;
  background-color: #fff;
  overflow: hidden;
}

.home-page-content {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  padding-top: 25vh;
  padding: 1em;
  background-color: #fff;
}

.header {
  position: absolute;
  left: 1em;
  bottom: 1em;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}

.header h1 {
  text-transform: uppercase;
  font-weight: 600;
  font-size: 9rem;
  letter-spacing: -0.5rem;
  line-height: 0.75;
}

.header-line-1 {
  margin-left: 9rem;
}

.home-page-footer {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  padding: 1em;
  display: flex;
  align-items: flex-end;
  gap: 1em;
}

.hp-footer-col:nth-child(1) {
  flex: 2;
}

.hp-footer-col:nth-child(2) {
  flex: 1;
  display: flex;
  gap: 1em;
  height: 110px;
}

.product-preview-hero {
  flex: 1;
  clip-path: polygon(0% 0%, 0% 0%, 0% 100%, 0% 100%);
  cursor: pointer;
}

.product-preview-hero:nth-child(3) {
  display: none;
}

h1 {
  position: relative;
  clip-path: polygon(0 0, 100% 0, 100% 100%, 0% 100%);
}

h1 span {
  position: relative;
  display: block;
  transform: translateY(110%);
  will-change: transform;
}

.preloader-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100svh;
  overflow: hidden;
  pointer-events: none;
  z-index: 2;
}

.pre-loader {
  position: fixed;
  top: 0;
  width: 100%;
  height: 100%;
  clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0% 100%);
}

.loader {
  position: absolute;
  top: 0;
  width: 100%;
  height: 100%;
