  font-weight: 700;
  font-style: normal;
  src: url(./fonts/PPNeueMontreal-Bold.ttf) format("truetype");
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  font-family: "PP Neue Montreal";
}

html,
body {
  width: 100%;
  height: 100%;
  background-color: var(--bg);
  color: var(--text);
}

.container {
  padding: 12em 2em 4em 2em;
}

img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

h1 {
  color: var(--text);
  font-size: 64px;
  font-weight: 500;
  line-height: 110%;
  letter-spacing: -0.02em;
}

h2 {
  color: var(--text);
  font-size: 42px;
  font-weight: 600;
  line-height: 110%;
  letter-spacing: -0.01em;
}

h3 {
  color: var(--text);
  font-size: 28px;
  font-weight: 600;
  line-height: 120%;
  letter-spacing: -0.005em;
}

p {
  color: var(--text-secondary);
  font-size: 16px;
  font-weight: 600;
  line-height: 125%;
  letter-spacing: -0.0025em;
}

a {
  color: var(--text);
  text-decoration: none;
  font-size: 16px;
  font-weight: 600;
  line-height: 125%;
  letter-spacing: -0.0025em;
}

@media (max-width: 900px) {
  h1 {
    font-size: 36px;
  }

  h2 {
    font-size: 30px;
  }

  h3 {
    font-size: 24px;
  }

  p,
  a {
    font-size: 14px;
  }
}

.pre-loader {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: var(--bg);
  clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0% 100%);
  z-index: 100000;
}

.progress-bar {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 7px;
  background-color: var(--text);
  transform: scaleX(0);
  transform-origin: left;
}

.home-page {
  position: absolute;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  overflow: hidden;
}

.hero-img {
  position: absolute;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  overflow: hidden;
}

.hero-title {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 60%;
  text-align: center;
}

.hero-title .line {
  clip-path: polygon(0 0, 100% 0, 100% 100%, 0% 100%);
}

.hero-title h1 {
  position: relative;
  transform: translateY(70px);
  will-change: transform;
}

@media (max-width: 900px) {
  .hero-title {
    width: 90%;
  }
}

