import "./App.css";

import { Routes, Route, useLocation } from "react-router-dom";

import Menu from "./components/menu/Menu";
import Footer from "./components/footer/Footer";

import Home from "./pages/home/Home";
import About from "./pages/about/About";
import Works from "./pages/works/Works";
import Contact from "./pages/contact/Contact";
import Blog from "./pages/blog/Blog";

import SampleProject from "./pages/sampleproject/SampleProject";
import SampleBlog from "./pages/sampleblog/SampleBlog";

import { AnimatePresence } from "framer-motion";

function App() {
  const location = useLocation();
  const showFooter = location.pathname !== "/";

  return (
    <>
      <Menu />
      <AnimatePresence mode="wait">
        <Routes location={location} key={location.pathname}>
          <Route index element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/works" element={<Works />} />
          <Route path="/blog" element={<Blog />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/sample-project" element={<SampleProject />} />
          <Route path="/sample-blog" element={<SampleBlog />} />
        </Routes>
      </AnimatePresence>
      {showFooter && <Footer />}
    </>
  );
}

export default App;

@font-face {
  font-family: "Rosseta";
  src: url("/src/assets/fonts/Rosseta.otf") format("opentype");
  font-weight: 300;
  font-style: normal;
  font-display: swap;
}

@font-face {
  font-family: "Wremena";
