import { useEffect } from "react";
import { Routes, Route, useLocation } from "react-router-dom";
import "./App.css";

import Navbar from "./components/navbar/Navbar";
import Footer from "./components/footer/Footer";

import Home from "./pages/home/Home";
import Projects from "./pages/projects/Projects";
import Archive from "./pages/archive/Archive";
import Information from "./pages/information/Information";
import SampleProject from "./pages/sampleproject/SampleProject";

import { AnimatePresence } from "framer-motion";

function App() {
  const location = useLocation();

  const pageTitles = {
    "/": "Home | Aiden Brooks | CG MWT AUG 2024",
    "/projects": "Projects | Aiden Brooks | CG MWT AUG 2024",
    "/archive": "Archive | Aiden Brooks | CG MWT AUG 2024",
    "/information": "Information | Aiden Brooks | CG MWT AUG 2024",
    "/sample-project": "Sample Project | Aiden Brooks | CG MWT AUG 2024",
  };

  useEffect(() => {
    const currentTitle = pageTitles[location.pathname] || "Aiden Brooks";
    document.title = currentTitle;

    if (location.pathname !== "/archive") {
      setTimeout(() => {
        window.scrollTo(0, 0);
      }, 700);
    }
  }, [location.pathname]);

  return (
    <>
      <Navbar />
      <AnimatePresence mode="wait" initial={false}>
        <Routes location={location} key={location.pathname}>
          <Route index element={<Home />} />
          <Route path="/projects" element={<Projects />} />
          <Route path="/archive" element={<Archive />} />
          <Route path="/information" element={<Information />} />
          <Route path="/sample-project" element={<SampleProject />} />
        </Routes>
      </AnimatePresence>
      <Footer />
    </>
  );
}

export default App;

:root {
  --color-bg: #000;
  --color-text: #fff;
}

@font-face {
  font-family: "Notch Grotesk";
  font-weight: 300;
  src: url("./assets/fonts/NotchGrotesk-Light.ttf") format("truetype");
}

@font-face {
  font-family: "Notch Grotesk";
  font-weight: 500;
  src: url("./assets/fonts/NotchGrotesk-Regular.ttf") format("truetype");
}

@font-face {
  font-family: "Notch Grotesk";
  font-weight: 700;
  src: url("./assets/fonts/NotchGrotesk-Bold.ttf") format("truetype");
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  font-family: "Notch Grotesk";
}

::-webkit-scrollbar {
  display: none;
}

html,
body {
  width: 100%;
  height: 100%;
  background-color: var(--color-bg);
  color: var(--color-text);
}

h1 {
  font-size: 3.75em;
  margin: 0;
  padding: 0;
  line-height: 100%;
}

p,
a,
li {
  margin: 0;
  padding: 0;
  text-decoration: none;
  color: var(--color-text);
  font-size: 12px;
  font-weight: lighter;
  line-height: 125%;
}

img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

button {
  border: none;
  outline: none;
  padding: 0.75em 1.25em;
  font-size: 12px;
  color: var(--color-text);
  background-color: rgba(255, 255, 255, 0.125);
  border-radius: 2em;
  backdrop-filter: blur(10px);
  cursor: pointer;
  transition: 0.3s;
}

button:hover {
  background-color: rgba(255, 255, 255, 0.25);
}

ul {
  margin: 0;
  padding: 0;
  list-style: none;
}

li {
  font-size: 22px;
}

.container {
  width: 100%;
  height: 100%;
}

.divider {
  width: 100%;
  height: 1px;
  background-color: #fff;
  margin: 0.5em 0;
}

@media (max-width: 900px) {
  h1 {
    font-size: 2em;
  }
}

import { StrictMode } from "react";
import { createRoot } from "react-dom/client";
import App from "./App.jsx";
import "./index.css";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";

createRoot(document.getElementById("root")).render(
  <Router>
    <Routes>
      <Route path="/*" element={<App />} />
    </Routes>
  </Router>
);

.footer {
  position: fixed;
  bottom: 0;
  left: 0;
