import { carouselItems, services, archive } from "../content/data.js";

document.addEventListener("DOMContentLoaded", () => {
  // lenis smooth scroll
  const lenis = new Lenis();
  lenis.on("scroll", ScrollTrigger.update);
  gsap.ticker.add((time) => {
    lenis.raf(time * 1000);
  });
  gsap.ticker.lagSmoothing(0);

  // time and date
  updateDateTime();
  setInterval(updateDateTime, 1000);

  function updateDateTime() {
    const now = new Date();
    const timeZone = Intl.DateTimeFormat().resolvedOptions().timeZone;
    const timeZoneAbbr = timeZone.split("/").pop().replace("_", " ");

    const timeOptions = {
      hour: "numeric",
      minute: "2-digit",
      hour12: true,
    };
    const timeStr =
      now.toLocaleTimeString("en-US", timeOptions) + ` [${timeZoneAbbr}]`;
    document.getElementById("current-time").textContent = timeStr;

    const dateOptions = {
      month: "long",
      day: "numeric",
      year: "numeric",
    };
    const dateStr = now.toLocaleDateString("en-US", dateOptions);
    document.getElementById("current-date").textContent = dateStr;
  }

  updateDateTime();
  setInterval(updateDateTime, 1000);

  // create services
  const serviceswrapper = document.querySelector(".services .wrapper");
  serviceswrapper.innerHTML = "";

  services.forEach((service) => {
    const serviceElement = document.createElement("div");
    serviceElement.className = "service";

    serviceElement.innerHTML = `
      <div class="index">
        <p>[${service.id}]</p>
      </div>
      <div class="title">
        <h3>${service.title}</h3>
      </div>
      <div class="copy">
        <p>${service.copy}</p>
      </div>
    `;

    serviceswrapper.appendChild(serviceElement);
  });

  // create carousel
  const carouselwrapper = document.querySelector(".carousel");

  carouselItems.forEach((item) => {
