          });
        } else {
          gsap.set(infoProgress, { transformOrigin: "left" });
          gsap.to(infoProgress, {
            scaleX: self.progress,
            duration: 0,
          });
        }
      } else if (self.direction < 0) {
        if (self.progress === 0) {
          gsap.set(infoProgress, { transformOrigin: "left" });
          gsap.to(infoProgress, {
            scaleX: 0,
            duration: 0.5,
            ease: "power2.inOut",
          });
        } else {
          gsap.set(infoProgress, { transformOrigin: "left" });
          gsap.to(infoProgress, {
            scaleX: self.progress,
            duration: 0,
          });
        }
      }
    },
