      `.archive-item:nth-child(${index + 1})`
    );
    new SplitType(archiveElement.querySelector(".archive-title h3"), {
      types: "chars",
      charClass: "char",
    });
  });

  gsap.set(".archive-shots .img", {
    position: "relative",
    opacity: 0,
    y: 40,
    willChange: "transform, opacity",
  });

  gsap.set(".archive-title .char", {
    position: "relative",
    opacity: 0,
    willChange: "opacity",
  });

  gsap.set(".archive-year p", {
    opacity: 0,
    willChange: "opacity",
  });

  archive.forEach((_, index) => {
    const archiveElement = document.querySelector(
      `.archive-item:nth-child(${index + 1})`
    );
    const images = archiveElement.querySelectorAll(".archive-shots .img");
    const chars = archiveElement.querySelectorAll(".archive-title .char");
    const year = archiveElement.querySelector(".archive-year p");

    ScrollTrigger.create({
      trigger: archiveElement,
      start: "top 100%",
      end: "bottom top",
      scrub: false,
      onEnter: () => {
        gsap.to(images, {
          opacity: 1,
          y: 0,
          duration: 0.5,
          stagger: { amount: 0.1 },
        });
        gsap.to(chars, {
          opacity: 1,
          duration: 0.05,
          stagger: { amount: 0.3 },
          delay: 0.1,
        });
        gsap.to(year, {
          opacity: 1,
          duration: 0.5,
          delay: 0.2,
        });
      },
      onEnterBack: () => {
        gsap.to(images, {
          opacity: 1,
          y: 0,
          duration: 0.5,
          stagger: { amount: 0.1 },
        });
        gsap.to(chars, {
          opacity: 1,
          duration: 0.05,
          stagger: { amount: 0.3 },
          delay: 0.1,
        });
        gsap.to(year, {
          opacity: 1,
          duration: 0.5,
          delay: 0.2,
        });
      },
      onLeaveBack: (self) => {
        if (self.direction < 0) {
          gsap.to(images, {
            opacity: 0,
            y: 40,
            duration: 0.25,
          });
          gsap.to(chars, {
            opacity: 0,
            duration: 0.25,
          });
          gsap.to(year, {
            opacity: 0,
            duration: 0.25,
          });
        }
      },
    });
  });

  // footer section header reveal animation
  new SplitType(".footer .outro h1", {
    types: "chars",
    charClass: "char",
  });

  gsap.set(".footer .outro .char", {
    position: "relative",
    y: 500,
    willChange: "transform",
  });

  ScrollTrigger.create({
    trigger: ".footer",
    start: "top 75%",
    end: "bottom bottom",
    scrub: false,
    onEnter: () => {
      gsap.to(".footer .outro .char", {
        y: 0,
        duration: 0.75,
        stagger: { amount: 0.3 },
        ease: "power2.out",
      });
    },
    onEnterBack: () => {
      gsap.to(".footer .outro .char", {
        y: 0,
        duration: 0.75,
        stagger: { amount: 0.3 },
        ease: "power2.out",
      });
    },
    onLeaveBack: () => {
      gsap.to(".footer .outro .char", {
        y: 500,
        duration: 0.25,
        ease: "power2.in",
      });
    },
  });

  // navigation click handling
  document.querySelectorAll(".nav-item").forEach((navItem) => {
    navItem.addEventListener("click", (e) => {
      e.preventDefault();

      const sectionId = navItem.id;
      let scrollTarget = 0;

      switch (sectionId) {
        case "hero":
          scrollTarget = 0;
          break;
        case "carousel":
          scrollTarget = window.innerHeight * 4;
          break;
        case "archive":
          scrollTarget = window.innerHeight * 10;
          break;
        case "footer":
          scrollTarget = window.innerHeight * 12;
          break;
      }

      lenis.scrollTo(scrollTarget, {
        duration: 2,
        easing: (t) => Math.min(1, 1.001 - Math.pow(2, -10 * t)),
      });
    });
  });
});
console.log("Script loaded");

// page transition
function animateBlocksBeforeUnload(event) {
  event.preventDefault();

  return new Promise((resolve) => {
    gsap.set(".block", { visibility: "visible", scaleY: 0 });
    gsap.to(".block", {
      scaleY: 1,
      duration: 0.5,
      stagger: 0.05,
      ease: "power2.inOut",
      onComplete: resolve,
    });
  });
}
console.log("Script loaded");

document.addEventListener("DOMContentLoaded", () => {
  console.log("DOM loaded");

  document.querySelectorAll("a").forEach((link) => {
    link.addEventListener("click", (event) => {
      event.preventDefault();

      const href = link.getAttribute("href");

      if (href && !href.startsWith("#") && href !== window.location.pathname) {
        animateTransition().then(() => {
          window.location.href = href;
        });
      }
    });
  });

  function animateTransition() {
    return new Promise((resolve) => {
      gsap.set(".block", { visibility: "visible", scaleY: 0 });
      gsap.to(".block", {
        scaleY: 1,
        duration: 0.5,
        stagger: 0.05,
        ease: "power2.inOut",
        onComplete: resolve,
      });
    });
  }

  gsap.set(".block", { clearProps: "all" });
});

document.addEventListener("DOMContentLoaded", () => {
  // lenis smooth scroll
  const lenis = new Lenis();
  lenis.on("scroll", ScrollTrigger.update);
  gsap.ticker.add((time) => {
    lenis.raf(time * 1000);
  });
  gsap.ticker.lagSmoothing(0);

  // sticky scroll
  gsap.to(".images", {
    yPercent: -80.5,
    ease: "none",
    scrollTrigger: {
      trigger: ".project-images",
      start: "top top",
      end: "+=300%",
      pin: true,
      scrub: true,
    },
  });

  // drawer animation
  const drawer = document.querySelector(".drawer");
  const drawerToggler = document.querySelector(".drawer-toggler");
  let isDrawerOpen = false;

  const drawerTl = gsap.timeline({
    paused: true,
    defaults: {
      duration: 1,
      ease: "power4.inOut",
    },
  });

  drawerTl.to(drawer, {
    x: 0,
  });

  function toggleDrawer() {
    isDrawerOpen = !isDrawerOpen;
    if (isDrawerOpen) {
      drawerTl.play();
    } else {
      drawerTl.reverse();
    }
  }

  drawerToggler.addEventListener("click", toggleDrawer);
});

