/* blog hero */

.blog-hero {
  width: 100%;
  height: 70vh;
  padding-top: 25vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
}

.blog-hero h1 {
  margin: 0.35em 0;
  font-size: 12vw;
  line-height: 0.8;
  text-transform: uppercase;
}

@media (max-width: 900px) {
  .blog-hero h1 {
    font-size: 20vw;
  }
}

/* blog content */

.blog-wrapper {
  margin: 5em 0;
  width: 100%;
}

.blog-wrapper .blog-content {
  display: flex;
  gap: 5em;
}

.blog-wrapper .blog-content .blog-img {
  flex: 2;
  border-radius: 10px;
  border: 2px solid var(--text-color);
  overflow: hidden;
}

.blog-wrapper .blog-content .blog-copy {
  flex: 2;
}

.blog-wrapper .blog-content h3 {
  margin-bottom: 0.5em;
  font-family: "Neue Montreal";
}

@media (max-width: 900px) {
  .blog-wrapper .blog-content {
    flex-direction: column;
  }
}

.blog-wrapper .blog-content img {
  width: 100%;
  object-fit: contain;
}

/* blog subheader */

.blog-subheader {
  width: 100%;
  margin: 10em 0;
}

.blog-subheader h3 {
  font-family: "Canopee";
  font-size: 7vw;
  font-weight: 500;
}

/* next  */

.next-btn {
  width: 100%;
  padding: 10em 0 20em 0;
}

.next-btn .next {
  display: flex;
  justify-content: center;
  align-items: center;
}

.next-btn .next a {
  text-decoration: none;
  color: var(--text-color);
  position: relative;
  top: -0.5vw;
  margin: 0 0.5em;
}

.next-icon {
  width: 4vw;
  height: 4vw;
}

.blog-item {
  margin: 10em 0;
  width: 100%;
  display: flex;
  gap: 5em;
  border-bottom: 1px solid rgba(255, 255, 255, 0.5);
  padding-bottom: 4em;
}

.blog-item .blog-title {
  flex: 2;
}

.blog-item .blog-title h2 {
  text-transform: uppercase;
  font-weight: 500;
  line-height: 0.9;
}

.blog-item .blog-data {
  flex: 3;
}

.blog-item .blog-title-sub p {
  font-size: 3.5vw;
  margin: 0.5em 0;
}

.blog-item .read {
  margin: 4em 0 2em 0;
  display: flex;
  justify-content: flex-start;
  align-items: center;
}

.blog-item .read a {
  text-decoration: none;
  color: var(--text-color);
  position: relative;
  top: -0.5vw;
  margin: 0 1em 0 0;
}

.read-icon {
  width: 3vw;
  height: 3vw;
}

@media (max-width: 900px) {
  .blog-item {
    flex-direction: column;
  }

  .blog-item .blog-title-sub p {
    font-size: 7vw;
  }
}

.contact {
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 10em;
}

/* header */

.contact .contact-header {
  flex: 1;
}

.contact .contact-copy {
  flex: 1;
}

.contact .contact-header h2 {
  font-size: 10vw;
  letter-spacing: -0.02em;
  line-height: 0.8;
}

.contact .contact-header h2 span {
  line-height: 0.8;
  font-weight: 500;
  font-family: "Ayer";
  text-transform: uppercase;
}

.contact .contact-header h2 span#italic {
  font-style: italic;
}

.contact .contact-header h5 {
  font-family: "Ayer";
  font-size: 3vw;
  font-weight: 500;
  width: 100%;
  text-align: right;
  margin: 0.4em 0;
}

/* content  */

.contact-copy .contact-copy-wrapper {
  display: flex;
  flex-direction: column;
  gap: 4em;
}

.contact-copy .contact-copy-wrapper .mail p {
  position: relative;
  color: var(--project-primary) !important;
  font-family: "Ayer";
  font-size: 3vw;
  width: max-content;
  color: var(--project-primary);
  opacity: 1;
}

.contact-copy .contact-copy-wrapper .mail p:after {
  content: "";
  position: absolute;
  top: 100%;
  left: 0;
  width: 100%;
  height: 1px;
  background: var(--project-primary);
}

.contact-copy .contact-copy-wrapper .socials {
  display: flex;
  gap: 2em;
  margin-bottom: 4em;
}

.contact-copy .contact-copy-wrapper .socials .social {
  margin: 1em 0;
  display: flex;
}

.contact-copy .contact-copy-wrapper .socials .social p {
  position: relative;
  margin: 0 0.5em;
  font-weight: 400;
  font-size: 1.5vw;
}

.contact-copy .contact-copy-wrapper .socials .social ion-icon {
  position: relative;
  top: 2px;
  font-size: 1.5vw;
}

.contact-copy .contact-copy-wrapper .newsletter {
  width: 100%;
}

.contact-copy .contact-copy-wrapper .newsletter h4 {
  font-family: "Canopee";
  margin: 0.5em 0;
}

.contact-copy .contact-copy-wrapper .newsletter span {
  font-family: "Acid Grotesk";
  color: var(--project-primary);
  font-size: 1vw;
}

.contact-copy .contact-copy-wrapper .newsletter .input {
  margin: 5em 0 0 0;
  width: 75%;
  display: flex;
  border-radius: 10px;
  border: 2px solid var(--text-color);
}

.contact-copy .contact-copy-wrapper .input input {
  flex: 2;
  border: none;
  background: none;
  font-family: "Acid Grotesk";
  font-size: 1.5vw;
  padding: 0.2em;
  color: var(--text-color);
}

.contact-copy .contact-copy-wrapper .input input:focus {
  border: none;
  outline: none;
}

.contact-copy .contact-copy-wrapper .submit {
  width: 40px;
  height: 40px;
}

@media (max-width: 900px) {
  .contact {
    margin-top: 20vh;
    flex-direction: column;
    height: max-content;
  }

  .contact .contact-header h2 {
    font-size: 20vw;
  }

  .contact .contact-header h5 {
    font-size: 7vw;
  }

  .contact-copy .contact-copy-wrapper {
    margin-bottom: 15em;
  }

  .contact-copy .contact-copy-wrapper .mail p {
    font-size: 10vw;
  }

  .contact-copy .contact-copy-wrapper .socials .social p {
    font-size: 4vw;
  }

  .contact-copy .contact-copy-wrapper .socials .social ion-icon {
    font-size: 4vw;
  }

  .contact-copy .contact-copy-wrapper .newsletter h4 {
    font-size: 8vw;
  }

  .contact-copy .contact-copy-wrapper .newsletter span {
    font-size: 3vw;
  }

  .contact-copy .contact-copy-wrapper .newsletter .input {
    width: 100%;
  }

  .contact-copy .contact-copy-wrapper .input input {
    font-size: 4vw;
  }
}

.credits {
  width: 75%;
  margin: 0 auto;
  height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
}

.credits .credits-header h1 {
  text-transform: uppercase;
  padding: 0 0 0.1em 0;
  font-size: 8vw;
  letter-spacing: -0.02em;
