:root {
  --bg: #e4e3db;
  --text-color: #e4e3db;
  --project-primary: #e88640;
  --project-footer: #0a0a0a;
}
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  line-height: 1;
}

h1::selection,
h2::selection,
h3::selection,
h4::selection,
h5::selection,
h6::selection,
p::selection,
a::selection {
  background: var(--text-color);
  color: var(--bg);
}

html,
body {
  width: 100%;
  height: 100%;
  background: var(--bg);
  color: var(--text-color);
  overflow-x: hidden;
}

/* container */
.container {
  width: 100vw;
}

.site-content {
  width: 75%;
  margin: 0 auto;
}

