}

/* text global styles */
h1,
h2,
h3,
h4 {
  font-family: "Canopee";
  font-weight: 400;
}

p,
a {
  font-family: "Acid Grotesk";
  font-weight: 400;
  line-height: 1.4;
  text-decoration: none;
}

p {
  opacity: 0.5;
}

h1 {
  font-size: 20vw;
}

h2 {
  font-size: 7vw;
}

h3 {
  font-size: 3vw;
}

h4 {
  font-size: 2.5vw;
}

p {
  font-size: 1.5vw;
}

a {
  font-size: 1.5vw;
}

@media (max-width: 900px) {
  h2 {
    font-size: 10vw;
  }

  h3 {
    font-size: 7vw;
  }

  h4 {
    font-size: 6vw;
  }

