/* hero */
.hero {
  position: relative;
  width: 100%;
  height: 150vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-bottom: 10em;
}

.hero .hero-header {
  padding-top: 20vh;
}

.hero .hero-header h1 {
  position: relative;
  top: 220px;
  line-height: 0.8;
  text-align: center;
  text-transform: uppercase;
  font-weight: 500;
}

.hero-header-wrapper {
  position: relative;
}

.about-h1-revealer {
  content: "";
  position: absolute;
  display: block;
  top: 200px;
  left: -50px;
  width: 125%;
  height: 155%;
  background: var(--bg);
}

.hero .hero-img {
  position: relative;
  width: 500px;
  height: 600px;
  margin-top: -25%;
  overflow: clip;
  clip-path: inset(0 0 0 0);
  opacity: 0;
  border-radius: 10px;
