/* footer */
footer {
  position: relative;
  width: 100vw;
  height: 85svh;
  color: var(--bg);
  padding: 2em;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
  overflow: hidden;
}

footer .footer-container {
  position: relative;
  width: 100%;
  height: 100%;
  background-color: var(--fg);
  padding: 2em;
  border-radius: 2em;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  overflow: hidden;
}

footer .footer-header {
  position: relative;
  text-align: center;
}

footer .footer-row {
  display: flex;
  gap: 2em;
  margin-bottom: 8em;
}

footer .footer-col {
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1em;
}

footer .footer-col a,
footer .copyright-info a {
  color: var(--bg);
}

footer .footer-col p:not(:first-child) {
  opacity: 0.35;
}

footer .copyright-info {
  position: relative;
  width: 100%;
  display: flex;
  justify-content: center;
  gap: 2em;
}

footer .footer-symbols {
  position: absolute;
  left: 0;
  width: 100%;
  padding: 2em;
  display: flex;
  justify-content: space-between;
}

footer .footer-symbols img {
  width: auto;
  height: 1rem;
}

footer .footer-symbols-1 {
  top: 0;
}

footer .footer-symbols-2 {
  bottom: 0;
}

footer .explosion-container {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 200%;
  pointer-events: none;
  overflow: hidden;
}

footer .explosion-particle-img {
  position: absolute;
  bottom: -200px;
  left: 50%;
  width: 150px;
  border-radius: 1rem;
  height: auto;
  object-fit: cover;
  transform: translateX(-50%);
  will-change: transform;
}

@media (max-width: 1000px) {
  footer {
    height: 100svh;
  }

  footer .copyright-info {
    flex-direction: column;
    text-align: center;
    gap: 0.5em;
  }

  footer .copyright-info p:nth-child(2) {
    display: none;
  }

  footer .footer-row {
    flex-direction: column;
    margin-bottom: 2em;
  }

  footer .footer-row .footer-col:nth-child(2),
  footer .footer-row .footer-col:nth-child(4) {
    display: none;
  }

  footer .explosion-container {
    display: none;
  }
}

:root {
  --bg: #edf1e8;
  --bg2: #d7dbd2;
  --fg: #141414;
  --accent1: #ed6a5a;
  --accent2: #f4f1bb;
  --accent3: #9bc1bc;
  --accent4: #5d576b;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  background-color: var(--bg);
  color: var(--fg);
}

img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

h1,
h2,
h3 {
  text-transform: uppercase;
  font-family: "rader";
  font-style: italic;
  line-height: 0.95;
}

h1 {
  font-size: 5rem;
}

h2 {
  font-size: 4rem;
}

h3 {
  font-size: 2rem;
}

a {
  text-decoration: none;
  color: var(--fg);
}

p,
p.ss {
  font-family: "formula-narrow";
  font-size: 1.25rem;
  font-weight: 600;
  line-height: 1.125;
}

p.mn {
  text-transform: uppercase;
  font-family: "supply-mono";
  font-size: 0.875rem;
  font-weight: 500;
  line-height: 1.125;
}

/* contact cta */
.contact-cta {
  position: relative;
  width: 100vw;
  height: 100svh;
  padding: 2em;
  display: flex;
  justify-content: center;
  align-items: center;
}

.contact-cta .contact-button {
  position: relative;
  width: 60%;
  height: 300px;
  border: 0.75em solid #000;
  border-radius: 20em;
  background: linear-gradient(
    45deg,
    var(--accent1),
    var(--accent2),
    var(--accent3),
    var(--accent4)
  );
  background-size: 400% 400%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 8px;
  transition: transform 0.2s ease;
  animation: gradientMove 10s ease-in-out infinite;
  box-shadow: 10px 10px 0px 5px #000000;
  overflow: hidden;
  cursor: pointer;
}

.contact-cta a {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100%;
  height: 100%;
  z-index: 2;
}

.contact-cta .contact-button::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 200%;
  height: 100%;
  background: repeating-linear-gradient(
    45deg,
    rgba(0, 0, 0, 0.125) 0px,
    rgba(0, 0, 0, 0.125) 15px,
    transparent 15px,
    transparent 30px
  );
  animation: stripeMove 30s linear infinite;
}

.contact-cta .contact-text-small {
  position: relative;
  z-index: 1;
}

.contact-cta .contact-text-large {
  position: relative;
  z-index: 1;
}

.contact-cta .contact-text-large h1 {
  font-size: 7rem;
}

@keyframes gradientMove {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}

@keyframes stripeMove {
  0% {
    transform: translateX(-50%);
  }
  100% {
    transform: translateX(0%);
  }
}

@media (max-width: 1000px) {
  h1 {
    font-size: 2.5rem;
  }

  .contact-cta {
    height: max-content;
    padding: 8em 2em;
  }

  .contact-cta .contact-button {
    width: 95%;
    height: 250px;
    gap: 1em;
    border-radius: 2em;
  }

  .contact-cta .contact-text-small {
    width: 75%;
    text-align: center;
  }

  .contact-cta .contact-text-large h1 {
    font-size: 3rem;
  }
}

/* home - hero */
.hero {
  position: relative;
  width: 100vw;
  height: 100svh;
  padding: 2em;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  overflow-x: hidden;
}

.hero .hero-header h1 {
  font-size: 20vw;
  line-height: 0.9;
}

.hero .hero-header.hero-header-1 {
  position: relative;
  transform: translateX(-20%);
  z-index: -1;
}

.hero .hero-header.hero-header-2 {
  position: relative;
  transform: translateX(20%);
  z-index: 2;
}

.hero .hero-footer {
  position: absolute;
  width: 100%;
  bottom: 0;
  padding: 2em;
