/* marquee */
.marquee {
  position: relative;
  width: 100vw;
  max-width: 100%;
  height: 240px;
  display: flex;
  align-items: center;
  font-size: 140px;
  text-transform: uppercase;
  overflow-x: hidden;
  color: var(--color-accent);
}

.track {
  position: absolute;
  white-space: nowrap;
  will-change: transform;
  animation: marquee 32s linear infinite;
}

@keyframes marquee {
  from {
    transform: translateX(0);
  }
  to {
    transform: translateX(-50%);
  }
}

/* skills */
.skills {
  width: 100%;
