  padding: 1em;
  overflow: hidden;
  transition: all 0.5s;
}

.skill::after {
  content: "";
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 125%;
  height: 125%;
  background: var(--color-accent);
  mix-blend-mode: difference;
  clip-path: circle(0% at 50% 50%);
  transition: all 0.5s;
}

.skill:hover::after {
  clip-path: circle(100% at 50% 50%);
