}

.product-hero > * {
  position: relative;
}

.product-hero p {
  font-size: 40px;
  width: 70%;
  margin-bottom: 2em;
  line-height: 100%;
}

.product {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.product > * {
  position: relative;
}

.product-title h1 {
  font-weight: 400;
  font-size: 100px;
  padding: 0.4em 0;
}

.product-title {
  font-size: 30px;
}

.product-title span {
  text-decoration: underline;
}

.product-look {
  position: relative;
}

.product-look img {
  width: 600px;
}

@media (max-width: 900px) {
  .product-hero p {
    width: 100%;
    font-size: 20px;
  }

  .product-title h1 {
    font-size: 40px;
    padding: 0;
  }

  .product-title {
    font-size: 16px;
  }

  .product-look img {
    width: 50vw;
  }
}

/* overlay */
.overlay {
  position: fixed;
  width: 100%;
  height: 100vh;
  background: rgba(0, 0, 0, 0.4);
  pointer-events: none;
  opacity: 0;
}

/* footer */

.product-info {
  position: absolute;
  background: #ecebec;
  bottom: 0;
  width: 100%;
  padding: 1em;
  border-top: 1px solid #000;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.product-nav,
.product-cart {
  display: flex;
  justify-content: center;
  align-items: center;
}

.product-nav > * {
  margin: 0 1em;
}

.product-img {
  width: 100px;
}

.product-img img {
  width: 100%;
}

.product-name {
  font-size: 24px;
}

.product-name span {
  font-size: 12px;
}

.product-variants {
  background: #fff;
  padding: 0.5em 1em;
  border-radius: 40px;
  font-size: 14px;
}

.product-nav-links {
  margin: 0 4em;
}

.product-nav-links > button {
  background: none;
  border: none;
  outline: none;
  margin: 0 1em;
  cursor: pointer;
}

.product-btn button {
  border: none;
  outline: none;
  background: #000;
  color: #fff;
  padding: 1.4em 2.8em;
  border-radius: 40px;
  margin-left: 2em;
  font-size: 14px;
  cursor: pointer;
}

@media (max-width: 900px) {
  .product-name,
  .product-img,
  .product-variants,
  .product-nav-links > button:nth-child(2) {
    display: none;
  }

  .product-nav-links {
    margin: 0;
  }
}

/* product-description */

.product-description {
  position: fixed;
  width: 100%;
  height: 50vh;
  bottom: -50vh;
  background: #ecebec;
  border-top: 1px solid #000;
  padding-bottom: 15vh;
  display: flex;
}

.close-desc {
  padding: 2em;
  cursor: pointer;
}

.desc {
  flex: 1;
  padding: 2em;
}

.link {
  margin: 2em 0;
}

.link span::after {
  position: relative;
  top: 0;
  left: 0;
  content: "";
  display: block;
  width: 100px;
  height: 2px;
  background: #000;
}

.front-view,
.side-view {
  flex: 2;
}

.view {
  padding: 2em 4em;
}

.product-description img {
  width: 400px;
}

.sketch-title span {
  font-weight: 500;
}

@media (max-width: 900px) {
  .front-view,
  .side-view {
    display: none;
  }
}

code.script.js:
document.addEventListener("DOMContentLoaded", function () {
  gsap.set(".img", { y: 500 });
  gsap.set(".loader-imgs", { x: 500 });
  gsap.set(".nav-item", { y: 25, opacity: 0 });
  gsap.set("h1, .item, footer", { y: 200 });

  const tl = gsap.timeline({ delay: 1 });

  tl.to(".img", {
    y: 0,
    duration: 1.5,
    stagger: 0.05,
    ease: "power3.inOut",
  })
    .to(
      ".loader-imgs",
      {
        x: 0,
        duration: 3,
        ease: "power3.inOut",
      },
      "-=2.5"
    )
    .to(
      ".img:not(#loader-logo)",
      {
        clipPath: "polygon(0% 0%, 100% 0%, 100% 0%, 0% 0%)",
        duration: 1,
        stagger: 0.1,
        ease: "power3.inOut",
      },
      "-=1"
    )
    .to(
      ".loader",
      {
        clipPath: "polygon(0% 0%, 100% 0%, 100% 0%, 0% 0%)",
        duration: 1,
        ease: "power3.inOut",
      },
      "-=0.5"
    )
    .to(
      ".nav-item, h1, footer, .item",
      {
        y: 0,
        opacity: 1,
        stagger: 0.1,
        duration: 1,
        ease: "power3.inOut",
      },
      "-=0.5"
    );
});

code.styles.css:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html,
body {
  font-family: "Sharp Grotesk";
  background: #e7e4e5;
  overflow: hidden;
}

.loader {
  position: fixed;
  width: 100vw;
  height: 100vh;
  background: #000;
  clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0% 100%);
  pointer-events: none;
}

.loader-imgs {
  width: 150%;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  display: flex;
  gap: 50px;
  clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0% 100%);
}

.img {
  position: relative;
  flex: 1;
  clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0% 100%);
}

img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

nav {
  width: 100%;
  padding: 30px;
  display: flex;
  align-items: center;
  font-family: "Neue Montreal";
}

.nav-item {
  position: relative;
  flex: 1;
}

.nav-item:nth-child(2) {
  text-align: center;
  font-family: "PP Editorial Old";
  font-size: 24px;
  font-weight: 500;
}

.nav-item:nth-child(3) {
  text-align: right;
}

#logo a {
  text-transform: none;
}

a {
  text-decoration: none;
  text-transform: uppercase;
  color: #000;
}

.hero {
  width: 100%;
  position: absolute;
  top: 45%;
  transform: translateY(-50%);
  text-transform: uppercase;
  font-family: "PP Migra";
}

.h1 {
  width: 100%;
  text-align: center;
  font-size: 4vw;
  clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0% 100%);
}

h1 {
  position: relative;
  font-weight: 400;
  line-height: 85%;
}

h1 span {
  font-family: "Sharp Grotesk";
  font-weight: 700;
}

footer {
  width: 100%;
  position: absolute;
  bottom: 30px;
  right: 30px;
  display: flex;
  justify-content: flex-end;
  gap: 10px;
  clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0% 100%);
}

.item {
  position: relative;
  width: 100px;
  height: 100px;
}
